import React, { createContext, useContext, useState, useEffect } from 'react';
import { auth, db } from '../firebase';
import { doc, setDoc, getDoc, updateDoc } from 'firebase/firestore';
import { RANKS, ACHIEVEMENTS, NOTIFICATIONS } from '../constants/badges';
import { useAuth } from './AuthContext';

const ProgressContext = createContext();

export function useProgress() {
  return useContext(ProgressContext);
}

export function ProgressProvider({ children }) {
  const { currentUser } = useAuth();
  const [userProgress, setUserProgress] = useState(null);
  const [loading, setLoading] = useState(true);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    if (currentUser) {
      const fetchProgress = async () => {
        const progressRef = doc(db, 'progress', currentUser.uid);
        const progressSnap = await getDoc(progressRef);

        if (progressSnap.exists()) {
          setUserProgress(progressSnap.data());
        } else {
          // Initialize progress document if it doesn't exist
          const initialProgress = {
            points: 0,
            quizzesTaken: 0,
            averageScore: 0,
            highestScore: 0,
            totalPoints: 0,
            badges: [],
            achievements: [],
            completedLessons: [],
            modulesCompleted: 0,
            completedModules: [],
            recentActivities: [],
            currentRank: RANKS.WOOD
          };
          await setDoc(progressRef, initialProgress);
          setUserProgress(initialProgress);
        }
        setLoading(false);
      };
      fetchProgress();
    }
  }, [currentUser]);

  useEffect(() => {
    if (currentUser) {
      localStorage.setItem('userProgress', JSON.stringify(userProgress));
      syncProgressWithFirebase();
    }
  }, [userProgress, currentUser]);

  const syncProgressWithFirebase = async () => {
    if (currentUser) {
      try {
        const progressRef = doc(db, 'progress', currentUser.uid);
        await setDoc(progressRef, userProgress);
      } catch (error) {
        console.error('Error syncing progress:', error);
      }
    }
  };

  const resetProgress = async () => {
    if (!currentUser) return;

    const initialProgress = {
      points: 0,
      quizzesTaken: 0,
      averageScore: 0,
      highestScore: 0,
      totalPoints: 0,
      badges: [],
      achievements: [],
      completedLessons: [],
      modulesCompleted: 0,
      completedModules: [],
      recentActivities: [],
      currentRank: RANKS.WOOD
    };

    try {
      const progressRef = doc(db, 'progress', currentUser.uid);
      await setDoc(progressRef, initialProgress);
      setUserProgress(initialProgress);
    } catch (error) {
      console.error('Error resetting progress:', error);
    }
  };

  const addNotification = (type, message) => {
    const newNotification = {
      id: Date.now(),
      type,
      message,
      timestamp: new Date()
    };
    setNotifications(prev => [newNotification, ...prev]);
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== newNotification.id));
    }, 5000);
  };

  const checkRankProgress = (points) => {
    const ranks = Object.values(RANKS);
    for (let i = ranks.length - 1; i >= 0; i--) {
      if (points >= ranks[i].minPoints && userProgress.currentRank.name !== ranks[i].name) {
        setUserProgress(prev => ({
          ...prev,
          currentRank: ranks[i]
        }));
        addNotification(NOTIFICATIONS.RANK_UP, `Congratulations! You've reached ${ranks[i].name} rank!`);
        break;
      }
    }
  };

  const checkAchievements = () => {
    const { modulesCompleted, quizzesTaken, averageScore, completedModules } = userProgress;
    const unlockedAchievements = [];

    // First Steps
    if (modulesCompleted >= 1 && !hasAchievement(ACHIEVEMENTS.FIRST_STEPS.id)) {
      unlockedAchievements.push(ACHIEVEMENTS.FIRST_STEPS);
    }

    // Quick Learner
    if (modulesCompleted >= 3 && !hasAchievement(ACHIEVEMENTS.QUICK_LEARNER.id)) {
      unlockedAchievements.push(ACHIEVEMENTS.QUICK_LEARNER);
    }

    // Knowledge Seeker
    if (completedModules.length === 7 && !hasAchievement(ACHIEVEMENTS.KNOWLEDGE_SEEKER.id)) {
      unlockedAchievements.push(ACHIEVEMENTS.KNOWLEDGE_SEEKER);
    }

    // Quiz Master
    if (quizzesTaken >= 5 && averageScore >= 90 && !hasAchievement(ACHIEVEMENTS.QUIZ_MASTER.id)) {
      unlockedAchievements.push(ACHIEVEMENTS.QUIZ_MASTER);
    }

    unlockedAchievements.forEach(achievement => {
      addAchievement(achievement);
    });
  };

  const hasAchievement = (achievementId) => {
    return userProgress.achievements?.some(a => a.id === achievementId) || false;
  };

  const addAchievement = (achievement) => {
    setUserProgress(prev => {
      const newPoints = prev.totalPoints + achievement.points;
      checkRankProgress(newPoints);

      // Update both achievements and badges arrays
      const newAchievements = [...(prev.achievements || []), achievement];
      const newBadges = [...(prev.badges || []), {
        id: achievement.id,
        name: achievement.name,
        description: achievement.description,
        earnedAt: new Date().toISOString()
      }];

      return {
        ...prev,
        achievements: newAchievements,
        badges: newBadges,
        totalPoints: newPoints
      };
    });
    addNotification(
      NOTIFICATIONS.ACHIEVEMENT_UNLOCKED,
      `Achievement Unlocked: ${achievement.name} (+${achievement.points} points)`
    );
  };

  const updateModulesCompleted = async (moduleId) => {
    if (!currentUser || !userProgress) return;

    // Only update if the module isn't already completed
    if (!userProgress.completedModules.includes(moduleId)) {
      const updatedProgress = {
        ...userProgress,
        modulesCompleted: userProgress.modulesCompleted + 1,
        completedModules: [...userProgress.completedModules, moduleId]
      };

      try {
        const progressRef = doc(db, 'progress', currentUser.uid);
        await updateDoc(progressRef, updatedProgress);
        setUserProgress(updatedProgress);
        checkAchievements();

        addActivity({
          type: 'module_completed',
          title: `Module ${moduleId}`,
          timestamp: new Date()
        });
      } catch (error) {
        console.error('Error updating module completion:', error);
      }
    }
  };

  const updateTimeSpent = (minutes) => {
    setUserProgress(prev => ({
      ...prev,
      totalTimeSpent: prev.totalTimeSpent + minutes
    }));
  };

  const updateQuizStats = async (score, earnedPoints) => {
    if (!currentUser) return;

    const progressRef = doc(db, 'progress', currentUser.uid);
    const newQuizzesTaken = (userProgress.quizzesTaken || 0) + 1;
    const newAverageScore = Math.round(
      ((userProgress.averageScore || 0) * (userProgress.quizzesTaken || 0) + score) / newQuizzesTaken
    );
    const newHighestScore = Math.max(userProgress.highestScore || 0, score);
    const newTotalPoints = (userProgress.totalPoints || 0) + earnedPoints;

    const updatedProgress = {
      ...userProgress,
      quizzesTaken: newQuizzesTaken,
      averageScore: newAverageScore,
      highestScore: newHighestScore,
      totalPoints: newTotalPoints
    };

    await updateDoc(progressRef, updatedProgress);
    setUserProgress(updatedProgress);
    checkAchievements();
  };

  const addPoints = async (points) => {
    if (!currentUser) return;

    const progressRef = doc(db, 'progress', currentUser.uid);
    const newPoints = (userProgress.totalPoints || 0) + points;

    const updatedProgress = {
      ...userProgress,
      totalPoints: newPoints
    };

    await updateDoc(progressRef, updatedProgress);
    setUserProgress(updatedProgress);
    checkAchievements();
  };

  const formatTime = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const addActivity = (activity) => {
    const newActivity = {
      id: Date.now(),
      description: activity,
      timestamp: new Date()
    };
    setUserProgress(prev => ({
      ...prev,
      recentActivities: [newActivity, ...prev.recentActivities.slice(0, 9)]
    }));
  };

  const value = {
    userProgress,
    notifications,
    resetProgress,
    updateModulesCompleted,
    updateTimeSpent,
    updateQuizStats,
    addPoints,
    formatTime,
    addActivity,
    hasAchievement,
    checkAchievements,
    loading
  };

  return (
    <ProgressContext.Provider value={value}>
      {!loading && children}
    </ProgressContext.Provider>
  );
}
