import React, { useState, useEffect } from 'react';
import { useProgress } from '../contexts/ProgressContext';
import { useNavigate } from 'react-router-dom';
import { ACHIEVEMENTS } from '../constants/badges';

const questions = [
  {
    question: "What is the Preamble of the Indian Constitution often referred to as?",
    options: [
      "The heart of the Constitution",
      "The soul of the Constitution",
      "The key to the Constitution",
      "The introduction to the Constitution"
    ],
    correctAnswer: 1
  },
  {
    question: "Which of these is NOT a Fundamental Right guaranteed by the Indian Constitution?",
    options: [
      "Right to Equality",
      "Right to Property",
      "Right to Freedom",
      "Right to Constitutional Remedies"
    ],
    correctAnswer: 1
  },
  {
    question: "How many Fundamental Duties are currently enlisted in the Indian Constitution?",
    options: [
      "8 duties",
      "9 duties",
      "11 duties",
      "12 duties"
    ],
    correctAnswer: 2
  },
  {
    question: "Which article of the Indian Constitution abolishes Untouchability?",
    options: [
      "Article 15",
      "Article 16",
      "Article 17",
      "Article 18"
    ],
    correctAnswer: 2
  },
  {
    question: "When was the Constitution of India adopted?",
    options: [
      "15 August 1947",
      "26 January 1950",
      "26 November 1949",
      "30 January 1948"
    ],
    correctAnswer: 2
  }
];

function Quiz() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);
  const { updateQuizStats, addPoints } = useProgress();
  const navigate = useNavigate();

  useEffect(() => {
    // Reset quiz state when component mounts
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
    setSelectedAnswer(null);
    setIsAnswered(false);
  }, []);

  const handleAnswerClick = (answerIndex) => {
    if (isAnswered) return;

    setSelectedAnswer(answerIndex);
    setIsAnswered(true);

    if (answerIndex === questions[currentQuestion].correctAnswer) {
      setScore(score + 1);
    }

    setTimeout(() => {
      if (currentQuestion < questions.length - 1) {
        setCurrentQuestion(currentQuestion + 1);
        setSelectedAnswer(null);
        setIsAnswered(false);
      } else {
        const finalScore = ((score + (answerIndex === questions[currentQuestion].correctAnswer ? 1 : 0)) / questions.length) * 100;
        setShowScore(true);
        updateQuizStats(finalScore);
        addPoints(Math.round(finalScore), 'Quiz Completion');
      }
    }, 1500);
  };

  const getButtonClass = (index) => {
    if (!isAnswered) return 'bg-white hover:bg-gray-50';
    if (index === questions[currentQuestion].correctAnswer) return 'bg-green-500 text-white';
    if (index === selectedAnswer) return 'bg-red-500 text-white';
    return 'bg-white';
  };

  const restartQuiz = () => {
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
    setSelectedAnswer(null);
    setIsAnswered(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12">
      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow-lg p-6">
          {showScore ? (
            <div className="text-center">
              <h2 className="text-2xl font-bold text-gray-900 mb-4">Quiz Complete!</h2>
              <p className="text-xl text-gray-700 mb-6">
                You scored {score} out of {questions.length} ({Math.round((score / questions.length) * 100)}%)
              </p>
              <div className="space-x-4">
                <button
                  onClick={restartQuiz}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-base font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700"
                >
                  Try Again
                </button>
                <button
                  onClick={() => navigate('/learn')}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-base font-medium rounded-md text-indigo-700 bg-indigo-100 hover:bg-indigo-200"
                >
                  Back to Learning
                </button>
              </div>
            </div>
          ) : (
            <>
              <div className="mb-8">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-xl font-semibold text-gray-900">
                    Question {currentQuestion + 1} of {questions.length}
                  </h2>
                  <span className="text-sm text-gray-500">
                    Score: {score}/{currentQuestion}
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2.5">
                  <div
                    className="bg-indigo-600 h-2.5 rounded-full transition-all duration-500"
                    style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
                  ></div>
                </div>
              </div>

              <div className="mb-8">
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  {questions[currentQuestion].question}
                </h3>
                <div className="space-y-3">
                  {questions[currentQuestion].options.map((option, index) => (
                    <button
                      key={index}
                      onClick={() => handleAnswerClick(index)}
                      disabled={isAnswered}
                      className={`w-full text-left p-4 rounded-lg border-2 transition duration-150 ${getButtonClass(
                        index
                      )}`}
                    >
                      {option}
                    </button>
                  ))}
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default Quiz;
