import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // For admin user, we'll just set the user state
      setCurrentUser({ email: 'admin', role: 'admin' });
      setLoading(false);
    } else {
      setLoading(false);
    }
  }, []);

  const login = async (email, password) => {
    try {
      // Check for admin credentials
      if (email === 'admin' && password === 'admin') {
        const token = 'admin-token'; // Simple token for admin
        localStorage.setItem('token', token);
        setCurrentUser({ email: 'admin', role: 'admin' });
        return { success: true };
      }
      return { success: false, error: 'Invalid credentials' };
    } catch (error) {
      return { success: false, error: error.message || 'Login failed' };
    }
  };

  const register = async (userData) => {
    try {
      // For now, we'll only allow admin registration
      if (userData.email === 'admin' && userData.password === 'admin') {
        const token = 'admin-token';
        localStorage.setItem('token', token);
        setCurrentUser({ email: 'admin', role: 'admin' });
        return { success: true };
      }
      return { success: false, error: 'Only admin registration is allowed' };
    } catch (error) {
      return { success: false, error: error.message || 'Registration failed' };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
  };

  const value = {
    currentUser,
    login,
    register,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
