import React from 'react';
import { Link } from 'react-router-dom';
import { useProgress } from '../contexts/ProgressContext';
import { formatDistanceToNow } from 'date-fns';

function Dashboard() {
  const { 
    userProgress = {}, 
    recentActivities = [],
    totalPoints = 0,
    badgesEarned = 0,
    quizzesCompleted = 0,
    chaptersCompleted = 0 
  } = useProgress();

  const getActivityIcon = (type) => {
    switch (type) {
      case 'quiz_completed':
        return '📝';
      case 'module_completed':
        return '📚';
      case 'badge_earned':
        return '🏆';
      case 'points_earned':
        return '⭐';
      case 'trivia_played':
        return '🎮';
      default:
        return '📌';
    }
  };

  const getActivityMessage = (activity) => {
    switch (activity.type) {
      case 'quiz_completed':
        return `Completed quiz "${activity.title}" with score ${activity.score}`;
      case 'module_completed':
        return `Completed module "${activity.title}"`;
      case 'badge_earned':
        return `Earned badge "${activity.badgeName}"`;
      case 'points_earned':
        return `Earned ${activity.points} points from ${activity.source}`;
      case 'trivia_played':
        return `Played trivia "${activity.gameTitle}" - ${activity.result}`;
      default:
        return activity.message || 'No recent activity';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Welcome Section */}
        <div className="bg-indigo-600 rounded-lg shadow-lg p-6 mb-8">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-white">
                Welcome back, {userProgress.email || 'User'}!
              </h1>
              <p className="text-indigo-100 mt-1">
                Continue your learning journey
              </p>
            </div>
            <div className="bg-white rounded-lg p-4 text-center">
              <p className="text-sm text-gray-600">Overall Progress</p>
              <p className="text-3xl font-bold text-indigo-600">
                {Math.round((chaptersCompleted / 7) * 100)}%
              </p>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Link to="/learn" className="bg-white rounded-lg shadow p-6 hover:shadow-lg transition">
            <div className="text-2xl mb-2">📚</div>
            <h3 className="text-lg font-semibold">Continue Learning</h3>
            <p className="text-gray-600">Pick up where you left off</p>
          </Link>

          <Link to="/quiz" className="bg-white rounded-lg shadow p-6 hover:shadow-lg transition">
            <div className="text-2xl mb-2">❓</div>
            <h3 className="text-lg font-semibold">Daily Quiz</h3>
            <p className="text-gray-600">Test your knowledge</p>
          </Link>

          <Link to="/trivia" className="bg-white rounded-lg shadow p-6 hover:shadow-lg transition">
            <div className="text-2xl mb-2">🎮</div>
            <h3 className="text-lg font-semibold">Play Trivia</h3>
            <p className="text-gray-600">Challenge others</p>
          </Link>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Achievements Section */}
          <div className="lg:col-span-2 bg-white rounded-lg shadow p-6">
            <h2 className="text-xl font-semibold mb-4">Achievements</h2>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="text-center p-4 bg-gray-50 rounded-lg">
                <p className="text-3xl font-bold text-indigo-600">{badgesEarned}</p>
                <p className="text-gray-600">Badges</p>
              </div>
              <div className="text-center p-4 bg-gray-50 rounded-lg">
                <p className="text-3xl font-bold text-indigo-600">{totalPoints}</p>
                <p className="text-gray-600">Points</p>
              </div>
              <div className="text-center p-4 bg-gray-50 rounded-lg">
                <p className="text-3xl font-bold text-indigo-600">{quizzesCompleted}</p>
                <p className="text-gray-600">Quizzes</p>
              </div>
              <div className="text-center p-4 bg-gray-50 rounded-lg">
                <p className="text-3xl font-bold text-indigo-600">{chaptersCompleted}</p>
                <p className="text-gray-600">Chapters</p>
              </div>
            </div>
          </div>

          {/* Recent Activity Section */}
          <div className="bg-white rounded-lg shadow p-6">
            <h2 className="text-xl font-semibold mb-4">Recent Activity</h2>
            <div className="space-y-4">
              {recentActivities.slice(0, 5).map((activity, index) => (
                <div key={index} className="flex items-start space-x-3 p-3 bg-gray-50 rounded-lg">
                  <div className="text-2xl">{getActivityIcon(activity.type)}</div>
                  <div className="flex-1">
                    <p className="text-sm text-gray-900">
                      {getActivityMessage(activity)}
                    </p>
                    <p className="text-xs text-gray-500 mt-1">
                      {formatDistanceToNow(new Date(activity.timestamp), { addSuffix: true })}
                    </p>
                  </div>
                </div>
              ))}
              {recentActivities.length === 0 && (
                <p className="text-gray-500 text-center py-4">No recent activity</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
