import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useProgress } from '../contexts/ProgressContext';
import { ACHIEVEMENTS } from '../constants/badges';

function Profile() {
  const { currentUser } = useAuth();
  const { userProgress } = useProgress();

  // Calculate total points
  const totalPoints = userProgress.points;

  // Calculate level based on points
  const level = Math.floor(totalPoints / 100) + 1;

  // Calculate progress to next level
  const pointsToNextLevel = 100 - (totalPoints % 100);

  // Calculate earned achievements
  const earnedAchievements = ACHIEVEMENTS.filter(achievement => {
    if (achievement.type === 'points' && totalPoints >= achievement.requirement) {
      return true;
    }
    if (achievement.type === 'modules' && userProgress.modulesCompleted >= achievement.requirement) {
      return true;
    }
    if (achievement.type === 'time' && userProgress.timeSpent >= achievement.requirement) {
      return true;
    }
    return false;
  });

  return (
    <div className="min-h-screen bg-gray-100 py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Profile Header */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex items-center space-x-4">
            <div className="bg-indigo-500 rounded-full w-16 h-16 flex items-center justify-center text-white text-2xl font-bold">
              {currentUser?.email?.charAt(0).toUpperCase()}
            </div>
            <div>
              <h2 className="text-2xl font-bold text-gray-900">{currentUser?.email}</h2>
              <p className="text-gray-500">Level {level} Scholar</p>
            </div>
          </div>
        </div>

        {/* Progress Section */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Learning Progress</h3>
            <div className="space-y-4">
              <div>
                <div className="flex justify-between text-sm text-gray-600 mb-1">
                  <span>Level Progress</span>
                  <span>{100 - pointsToNextLevel}/100 XP</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className="bg-indigo-500 rounded-full h-2"
                    style={{ width: `${100 - pointsToNextLevel}%` }}
                  ></div>
                </div>
                <p className="text-sm text-gray-500 mt-1">
                  {pointsToNextLevel} points to Level {level + 1}
                </p>
              </div>
              <div>
                <div className="flex justify-between text-sm text-gray-600 mb-1">
                  <span>Modules Completed</span>
                  <span>{userProgress.modulesCompleted}/7</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className="bg-green-500 rounded-full h-2"
                    style={{ width: `${(userProgress.modulesCompleted / 7) * 100}%` }}
                  ></div>
                </div>
              </div>
              <div>
                <div className="flex justify-between text-sm text-gray-600 mb-1">
                  <span>Time Spent Learning</span>
                  <span>{userProgress.timeSpent} minutes</span>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-lg p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Statistics</h3>
            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-50 p-4 rounded-lg">
                <div className="text-2xl font-bold text-indigo-600">{totalPoints}</div>
                <div className="text-sm text-gray-500">Total Points</div>
              </div>
              <div className="bg-gray-50 p-4 rounded-lg">
                <div className="text-2xl font-bold text-indigo-600">{level}</div>
                <div className="text-sm text-gray-500">Current Level</div>
              </div>
              <div className="bg-gray-50 p-4 rounded-lg">
                <div className="text-2xl font-bold text-indigo-600">
                  {userProgress.modulesCompleted}
                </div>
                <div className="text-sm text-gray-500">Modules Done</div>
              </div>
              <div className="bg-gray-50 p-4 rounded-lg">
                <div className="text-2xl font-bold text-indigo-600">
                  {earnedAchievements.length}
                </div>
                <div className="text-sm text-gray-500">Achievements</div>
              </div>
            </div>
          </div>
        </div>

        {/* Achievements Section */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Achievements</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {ACHIEVEMENTS.map((achievement, index) => {
              const isEarned = earnedAchievements.includes(achievement);
              return (
                <div
                  key={index}
                  className={`p-4 rounded-lg border-2 ${
                    isEarned ? 'border-green-500 bg-green-50' : 'border-gray-200'
                  }`}
                >
                  <div className="flex items-center space-x-3">
                    <div
                      className={`text-2xl ${
                        isEarned ? 'opacity-100' : 'opacity-50'
                      }`}
                    >
                      {achievement.icon}
                    </div>
                    <div>
                      <h4 className="font-semibold text-gray-900">
                        {achievement.name}
                      </h4>
                      <p className="text-sm text-gray-500">
                        {achievement.description}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
