import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function Navbar() {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/login');
    } catch (error) {
      console.error('Failed to log out:', error);
    }
  };

  return (
    <nav className="bg-indigo-600 text-white shadow-lg">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          <Link to="/home" className="text-xl font-bold">
            Gamified Samvidhan
          </Link>

          <div className="flex space-x-4">
            <Link to="/home" className="hover:text-indigo-200">
              Home
            </Link>
            <Link to="/learn" className="hover:text-indigo-200">
              Learn
            </Link>
            <Link to="/quiz" className="hover:text-indigo-200">
              Quiz
            </Link>
            <Link to="/trivia" className="hover:text-indigo-200">
              Trivia
            </Link>
            <Link to="/badges" className="hover:text-indigo-200">
              Badges
            </Link>
            <Link to="/leaderboard" className="hover:text-indigo-200">
              Leaderboard
            </Link>

            {currentUser ? (
              <>
                <Link to="/profile" className="hover:text-indigo-200">
                  Profile
                </Link>
                <button
                  onClick={handleLogout}
                  className="hover:text-indigo-200"
                >
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="hover:text-indigo-200">
                  Login
                </Link>
                <Link to="/register" className="hover:text-indigo-200">
                  Register
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
