import { initializeApp } from 'firebase/app';
import { getAuth } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyDqxLTrHJnGYZXBGsGXXnPXrHkKwXVDgBo",
  authDomain: "constitution-learning-app.firebaseapp.com",
  projectId: "constitution-learning-app",
  storageBucket: "constitution-learning-app.appspot.com",
  messagingSenderId: "1098376280578",
  appId: "1:1098376280578:web:5d8b5d8b5d8b5d8b5d8b5d"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Initialize Firebase Authentication
export const auth = getAuth(app);

// Initialize Firestore
export const db = getFirestore(app);

// Export default app instance
export default app;
