import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Dashboard from './pages/Dashboard';
import Login from './pages/Login';
import Register from './pages/Register';
import LearningModule from './pages/LearningModule';
import Quiz from './pages/Quiz';
import Trivia from './pages/Trivia';
import Profile from './pages/Profile';

// Protected Route wrapper component
const ProtectedRoute = ({ children }) => {
  const { currentUser } = useAuth();
  
  if (!currentUser) {
    return <Navigate to="/login" />;
  }

  return children;
};

// Root Route wrapper component (redirects based on auth status)
const RootRoute = () => {
  const { currentUser } = useAuth();
  
  if (currentUser) {
    return <Navigate to="/dashboard" />;
  }

  return <Navigate to="/login" />;
};

function App() {
  const { currentUser } = useAuth();

  return (
    <AuthProvider>
      <Router>
        <div className="min-h-screen bg-gray-50">
          {currentUser && <Navbar />} {/* Only show navbar when user is logged in */}
          <Routes>
            {/* Root route - redirects to login or dashboard based on auth status */}
            <Route path="/" element={<RootRoute />} />

            {/* Auth routes - accessible only when logged out */}
            <Route path="/login" element={
              currentUser ? <Navigate to="/dashboard" /> : <Login />
            } />
            <Route path="/register" element={
              currentUser ? <Navigate to="/dashboard" /> : <Register />
            } />

            {/* Protected routes - require authentication */}
            <Route path="/dashboard" element={
              <ProtectedRoute>
                <Dashboard />
              </ProtectedRoute>
            } />
            <Route path="/learn" element={
              <ProtectedRoute>
                <LearningModule />
              </ProtectedRoute>
            } />
            <Route path="/quiz" element={
              <ProtectedRoute>
                <Quiz />
              </ProtectedRoute>
            } />
            <Route path="/trivia" element={
              <ProtectedRoute>
                <Trivia />
              </ProtectedRoute>
            } />
            <Route path="/profile" element={
              <ProtectedRoute>
                <Profile />
              </ProtectedRoute>
            } />

            {/* Catch all - redirect to login or dashboard */}
            <Route path="*" element={<RootRoute />} />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
