import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useProgress } from '../contexts/ProgressContext';

const quizQuestions = [
  {
    id: 1,
    question: 'What is the Preamble to the Constitution?',
    options: [
      'A table of contents',
      'An introductory statement expressing the values and aspirations of the Constitution',
      'A list of fundamental rights',
      'A summary of constitutional amendments'
    ],
    correctAnswer: 1,
    explanation: 'The Preamble is an introductory statement that spells out the guiding principles of the Constitution, expressing its aims and objectives.'
  },
  {
    id: 2,
    question: 'Which of these is NOT a fundamental right guaranteed by the Indian Constitution?',
    options: [
      'Right to Equality',
      'Right to Freedom',
      'Right to Property',
      'Right to Constitutional Remedies'
    ],
    correctAnswer: 2,
    explanation: 'Right to Property was originally a fundamental right but was removed by the 44th Amendment in 1978. It is now a legal right under Article 300A.'
  },
  {
    id: 3,
    question: 'What is the basic structure doctrine?',
    options: [
      'A guide for constructing government buildings',
      'A principle that certain basic features of the Constitution cannot be altered',
      'A framework for organizing government departments',
      'A method for amending the Constitution'
    ],
    correctAnswer: 1,
    explanation: 'The basic structure doctrine, established in the Kesavananda Bharati case (1973), states that Parliament cannot alter the basic features of the Constitution.'
  },
  {
    id: 4,
    question: 'Which of these is a Fundamental Duty of Indian citizens?',
    options: [
      'To pay taxes regularly',
      'To vote in every election',
      'To protect and improve the natural environment',
      'To attend government meetings'
    ],
    correctAnswer: 2,
    explanation: 'Protecting and improving the natural environment is one of the Fundamental Duties added by the 42nd Amendment in 1976.'
  },
  {
    id: 5,
    question: 'What is the purpose of Directive Principles of State Policy?',
    options: [
      'To provide fundamental rights to citizens',
      'To guide state policy in governance',
      'To outline emergency provisions',
      'To define citizenship criteria'
    ],
    correctAnswer: 1,
    explanation: 'Directive Principles of State Policy are guidelines for the government to consider while framing laws and policies, aimed at ensuring social and economic democracy.'
  }
];

function Quiz() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showExplanation, setShowExplanation] = useState(false);
  const [score, setScore] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const navigate = useNavigate();
  const { addPoints } = useProgress();

  const handleAnswerSelect = (answerIndex) => {
    if (selectedAnswer === null) {
      setSelectedAnswer(answerIndex);
      if (answerIndex === quizQuestions[currentQuestion].correctAnswer) {
        setScore(score + 1);
      }
      setShowExplanation(true);
    }
  };

  const handleNextQuestion = () => {
    if (currentQuestion < quizQuestions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswer(null);
      setShowExplanation(false);
    } else {
      setQuizCompleted(true);
    }
  };

  useEffect(() => {
    if (quizCompleted) {
      const points = score * 20; // 20 points per correct answer
      addPoints(points, 'Completed quiz');
    }
  }, [quizCompleted, score, addPoints]);

  const getButtonClass = (index) => {
    if (selectedAnswer === null) {
      return 'bg-white hover:bg-gray-50';
    }
    if (index === quizQuestions[currentQuestion].correctAnswer) {
      return 'bg-green-100 border-green-500';
    }
    if (index === selectedAnswer) {
      return 'bg-red-100 border-red-500';
    }
    return 'bg-white';
  };

  if (quizCompleted) {
    return (
      <div className="min-h-screen bg-gray-100 py-6">
        <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Quiz Completed!</h2>
            <p className="text-lg text-gray-700 mb-4">
              You scored {score} out of {quizQuestions.length} questions correctly.
            </p>
            <p className="text-gray-600 mb-6">
              You've earned {score * 20} points for your performance!
            </p>
            <div className="flex space-x-4">
              <button
                onClick={() => navigate('/learn')}
                className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
              >
                Back to Learning
              </button>
              <button
                onClick={() => {
                  setCurrentQuestion(0);
                  setSelectedAnswer(null);
                  setShowExplanation(false);
                  setScore(0);
                  setQuizCompleted(false);
                }}
                className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-300"
              >
                Take Quiz Again
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 py-6">
      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="mb-6">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-2xl font-bold text-gray-900">
                Question {currentQuestion + 1} of {quizQuestions.length}
              </h2>
              <span className="text-gray-500">
                Score: {score}/{currentQuestion + (selectedAnswer !== null ? 1 : 0)}
              </span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-indigo-600 rounded-full h-2 transition-all duration-300"
                style={{
                  width: `${((currentQuestion + (selectedAnswer !== null ? 1 : 0)) /
                    quizQuestions.length) *
                    100}%`,
                }}
              ></div>
            </div>
          </div>

          <div className="mb-6">
            <h3 className="text-lg font-medium text-gray-900 mb-4">
              {quizQuestions[currentQuestion].question}
            </h3>
            <div className="space-y-3">
              {quizQuestions[currentQuestion].options.map((option, index) => (
                <button
                  key={index}
                  onClick={() => handleAnswerSelect(index)}
                  disabled={selectedAnswer !== null}
                  className={`w-full text-left p-4 rounded-lg border-2 transition-colors duration-200 ${getButtonClass(
                    index
                  )}`}
                >
                  {option}
                </button>
              ))}
            </div>
          </div>

          {showExplanation && (
            <div className="mb-6">
              <div
                className={`p-4 rounded-lg ${
                  selectedAnswer === quizQuestions[currentQuestion].correctAnswer
                    ? 'bg-green-50 border-2 border-green-500'
                    : 'bg-red-50 border-2 border-red-500'
                }`}
              >
                <h4 className="font-medium text-gray-900 mb-2">Explanation:</h4>
                <p className="text-gray-700">
                  {quizQuestions[currentQuestion].explanation}
                </p>
              </div>
            </div>
          )}

          {selectedAnswer !== null && (
            <button
              onClick={handleNextQuestion}
              className="w-full bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
            >
              {currentQuestion < quizQuestions.length - 1
                ? 'Next Question'
                : 'Finish Quiz'}
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default Quiz;
