import React, { createContext, useContext, useState, useEffect } from 'react';
import { useAuth } from './AuthContext';

const ProgressContext = createContext();

export function useProgress() {
  return useContext(ProgressContext);
}

export function ProgressProvider({ children }) {
  const { currentUser } = useAuth();
  const [userProgress, setUserProgress] = useState({
    modulesCompleted: 0,
    quizzesTaken: 0,
    averageScore: 0,
    totalPoints: 0,
    badges: [],
    recentActivities: []
  });

  // Reset progress
  const resetProgress = () => {
    setUserProgress({
      modulesCompleted: 0,
      quizzesTaken: 0,
      averageScore: 0,
      totalPoints: 0,
      badges: [],
      recentActivities: []
    });
  };

  // Update modules completed
  const updateModulesCompleted = (newCount) => {
    setUserProgress(prev => ({
      ...prev,
      modulesCompleted: newCount
    }));
    addActivity({
      type: 'module_completed',
      title: `Module ${newCount}`,
      timestamp: new Date()
    });
  };

  // Update quiz statistics
  const updateQuizStats = (score) => {
    setUserProgress(prev => {
      const newQuizCount = prev.quizzesTaken + 1;
      const newAverageScore = Math.round(
        ((prev.averageScore * prev.quizzesTaken) + score) / newQuizCount
      );
      return {
        ...prev,
        quizzesTaken: newQuizCount,
        averageScore: newAverageScore
      };
    });
    addActivity({
      type: 'quiz_completed',
      title: 'Quiz',
      score: score,
      timestamp: new Date()
    });
  };

  // Add points
  const addPoints = (points) => {
    setUserProgress(prev => ({
      ...prev,
      totalPoints: prev.totalPoints + points
    }));
  };

  // Add badge
  const addBadge = (badge) => {
    setUserProgress(prev => ({
      ...prev,
      badges: [...prev.badges, badge]
    }));
    addActivity({
      type: 'badge_earned',
      title: badge.name,
      timestamp: new Date()
    });
  };

  // Add activity
  const addActivity = (activity) => {
    setUserProgress(prev => ({
      ...prev,
      recentActivities: [activity, ...prev.recentActivities].slice(0, 5)
    }));
  };

  // Load progress from localStorage when component mounts or user changes
  useEffect(() => {
    if (currentUser) {
      const savedProgress = localStorage.getItem(`progress_${currentUser.uid}`);
      if (savedProgress) {
        setUserProgress(JSON.parse(savedProgress));
      }
    }
  }, [currentUser]);

  // Save progress to localStorage whenever it changes
  useEffect(() => {
    if (currentUser) {
      localStorage.setItem(`progress_${currentUser.uid}`, JSON.stringify(userProgress));
    }
  }, [userProgress, currentUser]);

  const value = {
    userProgress,
    resetProgress,
    updateModulesCompleted,
    updateQuizStats,
    addPoints,
    addBadge,
    addActivity
  };

  return (
    <ProgressContext.Provider value={value}>
      {children}
    </ProgressContext.Provider>
  );
}
