import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';

const sampleTriviaGames = [
  {
    id: 1,
    title: 'Constitution Speed Round',
    players: ['Player1', 'Player2', 'Player3'],
    maxPlayers: 4,
    status: 'waiting',
    difficulty: 'Easy'
  },
  {
    id: 2,
    title: 'Advanced Constitutional Quiz',
    players: ['Player1', 'Player2'],
    maxPlayers: 3,
    status: 'in-progress',
    difficulty: 'Hard'
  }
];

const sampleLeaderboard = [
  { rank: 1, name: 'Player1', score: 2500, wins: 15 },
  { rank: 2, name: 'Player2', score: 2200, wins: 12 },
  { rank: 3, name: 'Player3', score: 2000, wins: 10 },
];

function Trivia() {
  const { currentUser } = useAuth();
  const [activeGame, setActiveGame] = useState(null);
  const [gameMode, setGameMode] = useState('lobby'); // lobby, waiting, playing
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [timer, setTimer] = useState(0);
  const [playerStats, setPlayerStats] = useState({
    gamesPlayed: 45,
    gamesWon: 28,
    totalPoints: 3200,
    winRate: '62%'
  });

  useEffect(() => {
    if (gameMode === 'playing' && timer > 0) {
      const countdown = setInterval(() => {
        setTimer(t => t - 1);
      }, 1000);
      return () => clearInterval(countdown);
    }
  }, [gameMode, timer]);

  const joinGame = (game) => {
    setActiveGame(game);
    setGameMode('waiting');
    // Simulate waiting for other players
    setTimeout(() => {
      setGameMode('playing');
      setTimer(30);
      setCurrentQuestion({
        question: 'Which article of the Constitution deals with the President of India?',
        options: ['Article 52', 'Article 53', 'Article 54', 'Article 55'],
        correctAnswer: 0
      });
    }, 2000);
  };

  const createGame = () => {
    const newGame = {
      id: Date.now(),
      title: 'New Trivia Game',
      players: [currentUser?.email],
      maxPlayers: 4,
      status: 'waiting',
      difficulty: 'Medium'
    };
    setActiveGame(newGame);
    setGameMode('waiting');
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header with Stats */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Multiplayer Trivia</h1>
          <div className="grid grid-cols-4 gap-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-indigo-600">{playerStats.gamesPlayed}</p>
              <p className="text-sm text-gray-600">Games Played</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-indigo-600">{playerStats.gamesWon}</p>
              <p className="text-sm text-gray-600">Games Won</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-indigo-600">{playerStats.winRate}</p>
              <p className="text-sm text-gray-600">Win Rate</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-indigo-600">{playerStats.totalPoints}</p>
              <p className="text-sm text-gray-600">Total Points</p>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Game List/Active Game */}
          <div className="lg:col-span-2">
            {gameMode === 'lobby' ? (
              <div className="bg-white rounded-lg shadow-md p-6">
                <div className="flex justify-between items-center mb-6">
                  <h2 className="text-xl font-semibold">Available Games</h2>
                  <button
                    onClick={createGame}
                    className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700"
                  >
                    Create Game
                  </button>
                </div>
                <div className="space-y-4">
                  {sampleTriviaGames.map((game) => (
                    <div key={game.id} className="border rounded-lg p-4">
                      <div className="flex justify-between items-center">
                        <div>
                          <h3 className="font-semibold">{game.title}</h3>
                          <p className="text-sm text-gray-600">
                            Players: {game.players.length}/{game.maxPlayers} â€¢ Difficulty: {game.difficulty}
                          </p>
                        </div>
                        <button
                          onClick={() => joinGame(game)}
                          className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
                          disabled={game.status === 'in-progress'}
                        >
                          {game.status === 'waiting' ? 'Join Game' : 'In Progress'}
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ) : gameMode === 'waiting' ? (
              <div className="bg-white rounded-lg shadow-md p-6 text-center">
                <h2 className="text-xl font-semibold mb-4">Waiting for Players...</h2>
                <p className="text-gray-600">Game will start soon</p>
              </div>
            ) : (
              <div className="bg-white rounded-lg shadow-md p-6">
                <div className="flex justify-between items-center mb-6">
                  <div className="text-xl font-semibold">Question</div>
                  <div className="text-lg font-semibold">Time: {timer}s</div>
                </div>
                {currentQuestion && (
                  <div>
                    <h3 className="text-lg mb-4">{currentQuestion.question}</h3>
                    <div className="grid grid-cols-2 gap-4">
                      {currentQuestion.options.map((option, index) => (
                        <button
                          key={index}
                          className="p-4 text-left border-2 rounded-lg hover:border-indigo-500 hover:bg-indigo-50"
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Leaderboard */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Leaderboard</h2>
              <div className="space-y-4">
                {sampleLeaderboard.map((player) => (
                  <div key={player.rank} className="flex items-center justify-between">
                    <div className="flex items-center">
                      <span className="w-8 h-8 rounded-full bg-indigo-100 text-indigo-800 flex items-center justify-center font-semibold">
                        {player.rank}
                      </span>
                      <span className="ml-3 font-medium">{player.name}</span>
                    </div>
                    <div className="text-right">
                      <p className="font-semibold">{player.score}</p>
                      <p className="text-sm text-gray-600">{player.wins} wins</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Trivia;
