// Avatar images with metadata
const avatars = [
  {
    id: 1,
    name: 'Happy Face',
    path: '/avatars/avatar1.svg',
    style: 'simple',
    color: '#FFB6C1'
  },
  {
    id: 2,
    name: 'Cool Dude',
    path: '/avatars/avatar2.svg',
    style: 'simple',
    color: '#98FB98'
  },
  {
    id: 3,
    name: 'Star Eyes',
    path: '/avatars/avatar3.svg',
    style: 'simple',
    color: '#87CEEB'
  },
  {
    id: 4,
    name: 'Sunshine',
    path: '/avatars/avatar4.svg',
    style: 'simple',
    color: '#FFA07A'
  },
  {
    id: 5,
    name: 'Crown',
    path: '/avatars/avatar5.svg',
    style: 'simple',
    color: '#DDA0DD'
  },
  {
    id: 6,
    name: 'Wave',
    path: '/avatars/avatar6.svg',
    style: 'simple',
    color: '#20B2AA'
  },
  {
    id: 7,
    name: 'Kitty',
    path: '/avatars/avatar7.svg',
    style: 'animated',
    color: '#FFE4B5',
    animation: 'ear wiggle, eye blink'
  },
  {
    id: 8,
    name: 'Robot',
    path: '/avatars/avatar8.svg',
    style: 'animated',
    color: '#A9A9A9',
    animation: 'antenna beep, screen glow'
  },
  {
    id: 9,
    name: 'Alien',
    path: '/avatars/avatar9.svg',
    style: 'animated',
    color: '#90EE90',
    animation: 'float, glow, antenna wave'
  }
];

export const getRandomAvatar = () => {
  const randomIndex = Math.floor(Math.random() * avatars.length);
  return avatars[randomIndex].path;
};

export const getAvatarById = (id) => {
  return avatars.find(avatar => avatar.id === id)?.path || avatars[0].path;
};

export const getAvatarsByStyle = (style) => {
  return avatars.filter(avatar => avatar.style === style);
};

export const getAllAvatars = () => avatars;

export default avatars;
