import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';

const sampleQuizzes = [
  {
    id: 1,
    title: 'Daily Constitutional Quiz',
    questions: [
      {
        id: 1,
        question: 'Which article of the Indian Constitution abolishes untouchability?',
        options: ['Article 15', 'Article 16', 'Article 17', 'Article 18'],
        correctAnswer: 2
      },
      // More questions...
    ],
    timeLimit: 600, // 10 minutes
    points: 100
  },
  {
    id: 2,
    title: 'Fundamental Rights Challenge',
    questions: [
      {
        id: 1,
        question: 'Which fundamental right is known as the heart and soul of the Constitution?',
        options: [
          'Right to Equality',
          'Right to Freedom',
          'Right to Constitutional Remedies',
          'Right to Religion'
        ],
        correctAnswer: 2
      },
      // More questions...
    ],
    timeLimit: 900, // 15 minutes
    points: 150
  }
];

function Quiz() {
  const { currentUser } = useAuth();
  const [activeQuiz, setActiveQuiz] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(0);
  const [quizStats, setQuizStats] = useState({
    totalQuizzesTaken: 0,
    averageScore: 0,
    highestScore: 0,
    totalPoints: 0
  });

  useEffect(() => {
    if (activeQuiz && timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft((time) => time - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [activeQuiz, timeLeft]);

  const startQuiz = (quiz) => {
    setActiveQuiz(quiz);
    setCurrentQuestion(0);
    setScore(0);
    setTimeLeft(quiz.timeLimit);
  };

  const handleAnswer = (answerIndex) => {
    const question = activeQuiz.questions[currentQuestion];
    if (answerIndex === question.correctAnswer) {
      setScore(score + 1);
    }

    if (currentQuestion + 1 < activeQuiz.questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      // Calculate percentage score
      const percentageScore = Math.round((score / activeQuiz.questions.length) * 100);
      
      // Update quiz stats
      setQuizStats(prevStats => ({
        totalQuizzesTaken: prevStats.totalQuizzesTaken + 1,
        averageScore: Math.round(
          ((prevStats.averageScore * prevStats.totalQuizzesTaken) + percentageScore) / 
          (prevStats.totalQuizzesTaken + 1)
        ),
        highestScore: Math.max(prevStats.highestScore, percentageScore),
        totalPoints: prevStats.totalPoints + (percentageScore * activeQuiz.points / 100)
      }));
      
      setActiveQuiz(null);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Quiz Challenge</h1>
          <div className="grid grid-cols-4 gap-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-indigo-600">{quizStats.totalQuizzesTaken}</p>
              <p className="text-sm text-gray-600">Quizzes Taken</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-indigo-600">{quizStats.averageScore}%</p>
              <p className="text-sm text-gray-600">Average Score</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-indigo-600">{quizStats.highestScore}%</p>
              <p className="text-sm text-gray-600">Highest Score</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-indigo-600">{Math.round(quizStats.totalPoints)}</p>
              <p className="text-sm text-gray-600">Total Points</p>
            </div>
          </div>
        </div>

        {activeQuiz ? (
          <div className="bg-white rounded-lg shadow-md p-6">
            {/* Timer and Progress */}
            <div className="flex justify-between items-center mb-6">
              <div className="text-lg font-semibold">
                Question {currentQuestion + 1} of {activeQuiz.questions.length}
              </div>
              <div className="text-lg font-semibold">
                Time Left: {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}
              </div>
            </div>

            {/* Question */}
            <div className="mb-6">
              <h3 className="text-xl font-semibold mb-4">
                {activeQuiz.questions[currentQuestion].question}
              </h3>
              <div className="space-y-3">
                {activeQuiz.questions[currentQuestion].options.map((option, index) => (
                  <button
                    key={index}
                    onClick={() => handleAnswer(index)}
                    className="w-full text-left p-3 rounded-lg border-2 border-gray-200 hover:border-indigo-500 hover:bg-indigo-50 transition"
                  >
                    {option}
                  </button>
                ))}
              </div>
            </div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {sampleQuizzes.map((quiz) => (
              <div key={quiz.id} className="bg-white rounded-lg shadow-md p-6">
                <h3 className="text-xl font-semibold mb-2">{quiz.title}</h3>
                <p className="text-gray-600 mb-4">
                  Time: {quiz.timeLimit / 60} minutes â€¢ Points: {quiz.points}
                </p>
                <button
                  onClick={() => startQuiz(quiz)}
                  className="w-full bg-indigo-600 text-white py-2 rounded-lg hover:bg-indigo-700 transition"
                >
                  Start Quiz
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Quiz;
